import groovy.json.JsonSlurper
def Data= context.testCase.getTestStepByName("Login").getPropertyValue("Response")
def holder= new JsonSlurper().parseText(Data)
def token=holder.accessToken
context.testCase.testSuite.project.setPropertyValue("token" ,"bearer "+ token)

import groovy.json.JsonSlurper
def alok1 =context.testCase.getTestStepByName("get all users").getPropertyValue("Response")
def holder=new JsonSlurper().parseText(alok1)

def num=holder.count.toInteger()
log.info num

for(def i=0;i<num;i++)
{
if(holder.users[i].email=="Niharika26@gmail.com")
{
	def userid=holder.users[i]._id
	def converted_id= userid.toString()
	context.testCase.testSuite.project.setPropertyValue("userid",converted_id)
	log.info converted_id
	}
}

